mapper.insert:

    SELECT seq_comu_comment.NEXTVAL FROM dual

INSERT INTO tb_comu_comment (
comment_id, post_id, writer, content, created_at
) VALUES (
#{commentId}, #{postId}, #{writer}, #{content}, SYSDATE
)

mapper.select:
SELECT * FROM tb_comu_comment
WHERE post_id = #{postId}
ORDER BY comment_id ASC

mapper.update:
UPDATE tb_comu_post
SET write_count = (
SELECT COUNT(*) FROM tb_comu_comment WHERE post_id = #{postId}
)
WHERE post_id = #{postId}
